CREATE TABLE `online_makale` (
  `id` int(11) NOT NULL AUTO_INCREMENT,
  `makale_turu` int(11) DEFAULT NULL,
  `dergi_tanim` int(11) DEFAULT NULL,
  `makale_baslik` varchar(250) DEFAULT NULL,
  `makale_yazar` varchar(250) DEFAULT NULL,
  `mutercim` varchar(250) DEFAULT NULL,
  `makale_ozet` varchar(8192) DEFAULT NULL,
  `kaynakca` text DEFAULT NULL,
  `kaynak_dil` varchar(3) DEFAULT NULL,
  `anahtar_kelime` varchar(250) DEFAULT NULL,
  `yayin_created_at` date DEFAULT NULL,
  `kisaad` varchar(250) DEFAULT NULL,
  `dosya` varchar(50) DEFAULT NULL,
  `is_approved` tinyint(4) DEFAULT NULL,
  `view_count` int(11) NOT NULL DEFAULT 0,
  `download_count` int(11) NOT NULL DEFAULT 0,
  `created_at` date DEFAULT NULL,
  `created_by` int(11) DEFAULT NULL,
  `ip_address` varchar(100) DEFAULT NULL,
  `created_role` int(11) DEFAULT NULL,
  PRIMARY KEY (`id`) USING BTREE,
  KEY `idx_dergi_tanim` (`dergi_tanim`),
  KEY `idx_makale_baslik` (`makale_baslik`),
  KEY `idx_anahtar_kelime` (`anahtar_kelime`),
  KEY `fk_article_type` (`makale_turu`),
  KEY `idx_is_approved_online_makale` (`is_approved`),
  KEY `idx_view_count` (`view_count`),
  KEY `idx_download_count` (`download_count`),
  CONSTRAINT `fk_article_journal` FOREIGN KEY (`dergi_tanim`) REFERENCES `dergi_tanim` (`id`) ON DELETE CASCADE,
  CONSTRAINT `fk_article_type` FOREIGN KEY (`makale_turu`) REFERENCES `dergi_makale_tur` (`id`)
) ENGINE=InnoDB AUTO_INCREMENT=52 DEFAULT CHARSET=utf8mb3 COLLATE=utf8mb3_turkish_ci;
