--
-- Table structure for table `yonetim`
--

DROP TABLE IF EXISTS `yonetim`;
/*!40101 SET @saved_cs_client     = @@character_set_client */;
/*!40101 SET character_set_client = utf8mb4 */;
CREATE TABLE `yonetim` (
  `id` int(11) NOT NULL AUTO_INCREMENT,
  `ad_soyad` varchar(50) DEFAULT NULL,
  `sifre` varchar(255) NOT NULL,
  `grup_id` int(11) DEFAULT NULL,
  `email` varchar(100) DEFAULT NULL,
  `telefon` varchar(15) DEFAULT NULL,
  `adres` text DEFAULT NULL,
  `postakodu` varchar(6) DEFAULT NULL,
  `sehir` varchar(20) DEFAULT NULL,
  `ulke` varchar(100) DEFAULT NULL,
  `is_approved` tinyint(4) DEFAULT NULL,
  `is_active` tinyint(1) DEFAULT 1,
  `is_verified` tinyint(1) DEFAULT 0,
  `created_at` timestamp NULL DEFAULT current_timestamp(),
  `ip_address` varchar(50) DEFAULT NULL,
  `hashpass` varchar(50) DEFAULT NULL,
  `e_imza` text DEFAULT NULL,
  PRIMARY KEY (`id`),
  KEY `idx_email` (`email`),
  KEY `idx_grup_id` (`grup_id`),
  KEY `idx_user_email` (`email`),
  CONSTRAINT `fk_user_group` FOREIGN KEY (`grup_id`) REFERENCES `usergroup` (`id`),
  CONSTRAINT `fk_yonetim_grup` FOREIGN KEY (`grup_id`) REFERENCES `usergroup` (`id`) ON UPDATE CASCADE
) ENGINE=InnoDB AUTO_INCREMENT=46 DEFAULT CHARSET=utf8mb3 COLLATE=utf8mb3_turkish_ci;
/*!40101 SET character_set_client = @saved_cs_client */;

